import React, { useState } from 'react';
import { useHistory } from "react-router-dom";

export const AddExercisePage = () => {

    const [name, setName]=      useState('');
    const [date, setDate]=      useState('');
    const [reps, setReps]=      useState('');
    const [weight,setWeight]=   useState('');
    const [unit, setUnit]=      useState('');

    
    const history = useHistory();

    const addExercise = async () => {
        const newMovie = { name, reps, weight, unit, date };
        const response = await fetch('/', {
            method: 'post',
            body: JSON.stringify(newExercise),
            headers: {
                'Content-Type': 'application/json',
            },
        });
        if(response.status === 201){
            alert("Successfully added the Exercise!");
        } else {
            alert(`Failed to add movie, status code = ${response.status}`);
        }
        history.push("/");
    };


    return (
        <>
        <article>
            <h2>Add to the collection</h2>
            <p>Paragraph about this page.</p>
            <form onSubmit={(e) => { e.preventDefault();}}>
            <fieldset>
                    <legend>Which exercise are you adding?</legend>
                    <label for="name">Name</label>
                    <input
                        type="text"
                        value={name}
                        onChange={e => setName(e.target.value)} 
                        id="name" />
                    
                    <label for="date">date</label>
                    <input
                        type="date"
                        value={date}
                        onChange={e => setDate(e.target.value)} 
                        id="date" />

                    <label for="Reps">Repetitions</label>
                    <input
                        type="number"
                        value={reps}
                        onChange={e => setReps(e.target.value)} 
                        id="reps" />
                    
                    <label for="weight">Weight/ distance</label>
                    <input
                        type="number"
                        value={weight}
                        onChange={e => setWeight(e.target.value)} 
                        id="weight" />

                    <label for="unit">Unit</label>
                    <input
                        type="select"
                        value={unit}
                        onChange={e => setUnit(e.target.value)} 
                        id="name" />
                    <label for="submit">
                    <button
                        type="submit"
                        onClick={addMovie}
                        id="submit"
                    >Add</button> to the collection</label>
                </fieldset>
                </form>
            </article>
        </>
    );
}

export default AddMoviePage;
